import React, { Component } from 'react';
import './App.css';
import firebase from "firebase";
import StyledFirebaseAuth from "react-firebaseui/StyledFirebaseAuth";


firebase.initializeApp({
  apiKey: " AIzaSyBe4D-YbosgX24RLAey-6yzuN9XOco5Dzs",
  authDomain: "contacts-d6df6.firebaseapp.com"
})
class App extends Component {
  constructor(props) {
    super(props)
    this.state = {
      contacts: [],
      isLoaded:false,
    }
  }
  state = { 
    isSignedIn: false }
  uiConfig = {
    signInFlow: "popup",
    signInOptions: [
      firebase.auth.GoogleAuthProvider.PROVIDER_ID
    ],
    callbacks: {
      signInSuccess: () => false
    }
  }
  componentDidMount = () => {
          firebase.auth().onAuthStateChanged(user => {
            this.setState({ isSignedIn: !!user })
         //   console.log("user", user)
         // console.log(firebase.auth.GoogleAuthProvider.button)

         this.getContacts();
          ////////////////////////////test////////////////////////////////////  
         // var url = 'https://www.google.com/m8/feeds/contacts/' + user.email + '/full';
         // console.log("url", url)
        //  fetch(url)
         // .then(response => response.json())
         // .then(json => console.log(json))
          //.then(data => this.setState({ data }));
          fetch('https://www.googleapis.com/auth/contacts.readonly')
            .then(res => res.json())
            .then(json => {
              this.setState({
                isLoaded: true,
                items:json,
              })
            }); 

          })
    }
    //testcontacts//////////////
    getContacts()
    {
      GOOGLE_API_KEY='AIzaSyDHEZqD8OeOZQjqSnZH7AjjE0quXwExH6A';
      let that=this;
      function start() {
        gapi.client.init({
          'apiKey': GOOGLE_API_KEY
        }).then(function() {
          return gapi.client.request({
            'path': `https://www.googleapis.com/calendar/v3/calendars/${CALENDAR_ID}/events`,
          })
        }).then( (response) => {
          let contacts = response.result.items
          that.setState({
            contacts
          }, ()=>{
            console.log(that.state.contacts);
          })
        }, function(reason) {
          console.log(reason);
        });
      }
      gapi.load('client', start)
    }


///////////////////////////////////////////////HTML/////////////////////////////////////////////////////////////////////////////
  render() {
    var{
      isLoaded,
      items} =this.state ;

      if (!isLoaded){
        return<div>
          Loading...
        </div>
      }
      else {

    return (
      <div>
              Data has been loaded


      <div className="card">

      {
        this.state.isSignedIn ? (
        <span>
          <div className="text" >Signed In!</div>
          <button className="button" onClick={() => firebase.auth().signOut()}>Sign out!</button>
          <h1 className="text">Welcome {firebase.auth().currentUser.displayName}</h1>
          <img className="img"
              alt="profile picture" 
              src={firebase.auth().currentUser.photoURL}>
          </img>

        </span>
      ) : (
        <StyledFirebaseAuth
          uiConfig={this.uiConfig}
          firebaseAuth={firebase.auth()}
        />
      )}

</div>
</div>


    );
  }
  }
}

export default App;
